import React, { Component } from 'react';

class  DetailCellRenderer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      image: 'https://nana.sa'+props.data.image,
      barcode: props.data.barcode,
      price: props.data.price
    }

  }

  render() {
    return (
      <div style={{width: '100%',borderWidth: 1,height: 200,padding: 20,display: 'flex',flexDirection: 'row',justifyContent: 'space-between',alignItems: 'center',borderColor:'#ECF0F1',borderRadius: 8}}>
        <div style={{display: 'flex',flexDirection: 'column',justifyContent: 'center',alignItems: 'center',backgroundColor: 'red',width: '50%',height: '100%'}}>
          <div>
            <h1>صورة المنتج</h1>
          </div>
          <div>
            <img
              src={this.state.image}
              alt="صورة المنتج"
              style={{height: '80%',width: '80%'}}
            />
          </div>
        </div>
        <div style={{display: 'flex',flexDirection: 'column',backgroundColor: 'yellow',width: '25%',height: '100%',alignItems: 'center',justifyContent: 'space-between'}}>
          <h1>الباركود</h1>
          <p>{this.state.barcode}</p>
        </div>
        <div style={{display: 'flex',flexDirection: 'column',backgroundColor: 'red',width: '25%',height: '100%',alignItems: 'center',justifyContent: 'space-between'}}>
          <h1>السعر</h1>
          <h3>{this.state.price}</h3>
        </div>
      </div>
    );
  }

}

export default DetailCellRenderer;
