import React, { Component } from 'react';

class  DetailCellRenderer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      image: 'https://nana.sa'+props.data.image,
      barcode: props.data.barcode,
      price: props.data.price
    }

  }

  render() {
    return (
      <div style={{borderWidth: 1,height: 180,margin:20,padding: 20,display: 'flex',flexDirection: 'row',justifyContent: 'space-between',alignItems: 'center',borderColor:'#ECF0F1',borderRadius: 8}}>
        <div style={{display: 'flex',flexDirection: 'column',justifyContent: 'center',alignItems: 'center'}}>
          <div>
            <h1>صورة المنتج</h1>
          </div>
          <div>
            <img
              src={this.state.image}
              alt="صورة المنتج"
              style={{height: 150,width: 200}}
            />
          </div>
        </div>
        <div style={{display: 'flex',flexDirection: 'column'}}>
          <div style={{height: '10%'}}>
            <h1>الباركود</h1>
          </div>
          <div style={{height: '90%'}}>
            <p>{this.state.barcode}</p>
          </div>
        </div>
        <div style={{display: 'flex',flexDirection: 'column'}}>
          <div style={{height: '10%'}}>
            <h1>السعر</h1>
          </div>
          <div style={{height: '90%'}}>
            <h3>{this.state.price}</h3>
          </div>
        </div>
      </div>
    );
  }

}

export default DetailCellRenderer;
