import React, { Component } from 'react';
// import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import logo from './logo.svg';
import './App.css';


import { AgGridReact } from 'ag-grid-react';
import 'ag-grid-community/dist/styles/ag-grid.css'
import 'ag-grid-community/dist/styles/ag-theme-balham.css';

let data_to_arrange = [
  {
    "product_name_ar": "مناديل وجه سانيتا كلوب مزدوج 100 حبة",
    "last_scan_time": null,
    "price": 3.450000,
    "name_ar_view": "العناية بالوجه",
    "category_parent_name_ar_view": "العناية الشخصية",
    "title": "سانيتا"
  },
  {
    "product_name_ar": "خليط كيك بيتي كروكر اصفر 500 جرام",
    "last_scan_time": "2020-06-29",
    "price": 11.500000,
    "name_ar_view": "منتجات المخابز",
    "category_parent_name_ar_view": "مستلزمات الطبخ",
    "title": "بيتي كروكر"
  },
  {
    "product_name_ar": "خليط كيك بيتي كروكر اصفر 500 جرام",
    "last_scan_time": "2020-06-29",
    "price": 11.500000,
    "name_ar_view": "منتجات رمضان",
    "category_parent_name_ar_view": "احتياجات منزلية عامة",
    "title": "بيتي كروكر"
  },
  {
    "product_name_ar": "غسول جسم هيمالايا حمص وجرين جرام 200 مل",
    "last_scan_time": null,
    "price": 12.650000,
    "name_ar_view": "الصابون",
    "category_parent_name_ar_view": "العناية الشخصية",
    "title": "هيمالايا"
  }
]

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      columnDefs:[
        {headerName:'Product Name',field:'name', sortable:true,filter:true},
        {headerName:'Price',field:'price', sortable:true,filter:true},
        {headerName:'Category',field:'category', sortable:true,filter:true},
        {headerName:'Brand',field:'brand', sortable:true,filter:true}
      ],
      rowData:[
        { name: 'Toyota', price: 1500, category:'Laban',brand:'Maraie'}
      ]
    };
  }

  componentDidMount() {
    this.arrange_data(data_to_arrange)
  }

  arrange_data = async (data) => {
    console.log(data);
    let returned_data = []
    await data.map(item =>(
      returned_data.push({name:item.product_name_ar,price:item.price,category:item.category_parent_name_ar_view,brand:item.title})
    ))
    await this.setState({
      rowData:returned_data
    })
    console.log('done');
  }
  render() {
    return (
      <div>
        <div style={{height:110,backgroundColor:'#235434',justifyContent: 'space-between',paddingHorizontal: 20,flexDirection: 'row',padding: 10,marginBottom: 10}}>
          <img src='./logo.png' alt="Logo" style={{width: 60}}/>
        </div>
        <div className="container">
          <div
          className="ag-theme-balham"
          style={{
            width:'100%',
            height:600
          }}
          >
            <AgGridReact
            columnDefs={this.state.columnDefs}
            rowData={this.state.rowData}
            />
          </div>
        </div>
      </div>
    );
  }

}
export default App;
