import React, { Component } from 'react';
import './App.css';

class  DetailCellRenderer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      image: 'https://nana.sa'+props.data.image,
      barcode: props.data.barcode,
      price: props.data.price
    }

  }

  render() {
    return (
      <div style={{borderWidth: 1,height: 220,padding: 20,display: 'flex',flexDirection: 'row',margin:10,
        justifyContent: 'space-between',alignItems: 'center',borderColor:'#ECF0F1',borderRadius: 8,backgroundColor: '#F5F7F7'}}>

        <div className="boxShadow" style={{padding: 10,backgroundColor: 'white',borderRadius: 8,display: 'flex',flexDirection: 'column',justifyContent: 'space-between',alignItems: 'center',width: '40%',height: 180}}>
          <div>
            <h1 className="customFont">صورة المنتج</h1>
          </div>
          <div>
            <img
              src={this.state.image}
              alt="صورة المنتج"
              style={{height: 100,maxWidth: '90%'}}
            />
          </div>
        </div>
        <div className="boxShadow" style={{borderRadius: 8,padding: 10,display: 'flex',flexDirection: 'column',backgroundColor: 'white',width: '25%',height: '100%',alignItems: 'center',justifyContent: 'space-between'}}>
          <h1 className="customFont">الباركود</h1>
          <p className="customFont2">{this.state.barcode}</p>
        </div>
        <div style={{backgroundColor: 'white',borderRadius: 8,padding: 10,display: 'flex',flexDirection: 'column',width: '25%',height: '100%',alignItems: 'center',justifyContent: 'space-between'}}>
          <h1 className="customFont">السعر</h1>
          <h3 className="customFont2">{this.state.price}</h3>
        </div>
      </div>
    );
  }

}

export default DetailCellRenderer;
